import React, { useState } from "react";
import ReactDOM from "react-dom";

const App = props => {
  const [selected, setSelected] = useState(0);

  const setToSelected = newSelected => {
    setSelected(newSelected);
  };

  return (
    <div>
      {props.anecdotes[selected]}
      <p>
        <Table> {props.votes[selected]}</Table>
      </p>
      <p>{props.votes[selected]}</p>
      <Button2 handleClick={() => addVotes(selected)} />
      <Button
        handleClick={() => setToSelected(getRandomInt(anecdotes.length))}
      />
    </div>
  );
};

function getRandomInt(max) {
  const value = Math.floor(Math.random() * Math.floor(max));

  return value;
}

function addVotes(chosen) {
  const copy = [...votes];
  copy[chosen] += 1;
  const votes2 = [...copy];
  votes = votes2;
  console.log(votes[chosen]);
  return;
}

const Button = props => (
  <button onClick={props.handleClick}>Arvo satunnainen FS-anekdootti! </button>
);

const Button2 = props => <button onClick={props.handleClick}> Äänestä </button>;

const votes = [0, 0, 0, 0, 0, 0];
const anecdotes = [
  "If it hurts, do it more often",
  "Adding manpower to a late software project makes it later!",
  "The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.",
  "Any fool can write code that a computer can understand. Good programmers write code that humans can understand.",
  "Premature optimization is the root of all evil.",
  "Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it."
];
const Table = props => <p> {props.votes} Votes </p>;

ReactDOM.render(
  <App anecdotes={anecdotes} votes={votes} />,
  document.getElementById("root")
);
