import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = ({handleClick, text}) => (
    <button onClick={handleClick}>
    {text}
    </button>
)

const Graph = (props) => {
  return (
    <div> Anna mielipiteesti Unicafen palvelusta </div>
  )
}

const Tilastot = (props) => {
  return (
    <div><p>{props.allReviews}</p>{props.good}, {props.neutral}, {props.bad}</div>
  )
}

const App = (props) => {

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [allReviews, setAll] = useState([])

  const handleGoodClick = () => {
      setAll(allReviews.concat('H '))
      setGood(good + 1)
  }

  const handleBadClick = () => {
    setAll(allReviews.concat('P '))
      setBad(bad + 1)
  }

  const handleNeutralClick = () => {
    setAll(allReviews.concat('N '))
      setNeutral(neutral + 1)
  }
  return (
    <>
      <div>
      <Graph />

      <Button handleClick={handleGoodClick}
      text='Hyvää'
      />


      <Button handleClick={handleNeutralClick}
      text='Neutraali'
      />


      <Button handleClick={handleBadClick}
      text='Pahaa'
      />

      <Tilastot />
      </div>
    </>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
