import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
  </button>
)

const Statistic = ({ name, value }) => (

  <p>{name} {value}</p>

)

const Graph = (props) => {
  return (
    <div> <h1><strong>Anna mielipiteesi Unicafen palvelusta </strong></h1> </div>
  )
}

const Statistics = (props) => {
  if (props.good === 0 && props.neutral === 0 && props.bad === 0) {
    return (<div>Ei yhtään annettua palautetta</div>)
  }
  return (
    <div>
      <h1>T I L A S T O T</h1>
      <Statistic name='Hyvää' value={good} />
      <Statistic name='Neutraali' value={neutral} />
      <Statistic name='Pahaa' value={bad} />
      <Statistic name='Yhteensä' value={good + bad + neutral} />
      <Statistic name='Keskiarvo' value={(good - bad) / (good + props.neutral + props.bad)} />

      <Statistic name='Positiivisia' value={(props.good / (props.good + props.neutral + props.bad) * 100)} />
    </div>

  )
}

const App = (props) => {

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [allReviews, setAll] = useState([])

  const handleGoodClick = () => {

    setGood(good + 1)
  }

  const handleBadClick = () => {

    setBad(bad + 1)
  }

  const handleNeutralClick = () => {

    setNeutral(neutral + 1)
  }


  return (
    <>
      <div>
        <Graph />

        <Button handleClick={handleGoodClick}
          text='Hyvää'
        />


        <Button handleClick={handleNeutralClick}
          text='Neutraali'
        />


        <Button handleClick={handleBadClick}
          text='Pahaa'
        />


        <Statistics good={good} neutral={neutral} bad={bad} />
      </div>

    </>
  )
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
