  const course = 'Half Stack -sovelluskehitys'
  const parts = [
        {
            name: 'Reactin perusteet',
            exercises: 10
        },

        {
            name: 'Tiedonvälitys propseilla',
            exercises: 7
        },

        {
            name: 'Komponenttien tila',
            exercises: 14
        }
    ]


  return (
    <>
     <Header course={course}/>
     <Content parts={parts} />
     <Total parts={parts}/>
    </>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
